import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { iconMapping } from "../utils/iconMapping";
import { getExercisesByDay } from "../utils/exerciseUtils";

const ExerciseDetailScreen = () => {
  const { exerciseName } = useParams();
  const navigate = useNavigate();
  const [exercise, setExercise] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (exerciseName) {
      findExercise(decodeURIComponent(exerciseName));
    }
  }, [exerciseName]);

  const findExercise = (name) => {
    // Buscar el ejercicio en todos los días
    const days = ["Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado", "Domingo"];
    
    for (const day of days) {
      const dayData = getExercisesByDay(day);
      if (dayData && dayData.ejercicios) {
        for (const grupo of dayData.ejercicios) {
          if (grupo.rutina) {
            for (const ejercicio of grupo.rutina) {
              if (ejercicio.nombre === name) {
                // Encontramos el ejercicio, ahora construimos el objeto completo
                const exerciseDetail = {
                  ...ejercicio,
                  grupoMuscular: grupo.nombre,
                  dia: dayData.dia,
                  musculosDelDia: dayData.musculos,
                  descansoGeneral: dayData.descanso
                };
                setExercise(exerciseDetail);
                setLoading(false);
                return;
              }
            }
          }
        }
      }
    }
    
    // Si no se encuentra el ejercicio
    setExercise(null);
    setLoading(false);
  };

  const getDifficultyColor = (nivel) => {
    const colors = {
      "Principiante": "text-green-600",
      "Medio": "text-yellow-600", 
      "Alto": "text-red-600",
      "Avanzado": "text-red-700"
    };
    return colors[nivel] || "text-gray-600";
  };

  const getDifficultyBgColor = (nivel) => {
    const colors = {
      "Principiante": "bg-green-100",
      "Medio": "bg-yellow-100",
      "Alto": "bg-red-100", 
      "Avanzado": "bg-red-200"
    };
    return colors[nivel] || "bg-gray-100";
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-ios-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ios-blue mx-auto mb-4"></div>
          <p className="text-ios-gray-600">Cargando ejercicio...</p>
        </div>
      </div>
    );
  }

  if (!exercise) {
    return (
      <div className="min-h-screen bg-ios-gray-50">
        <div className="px-4 py-8">
          <button
            onClick={() => navigate(-1)}
            className="flex items-center text-ios-blue mb-6"
          >
            <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
            </svg>
            Volver
          </button>
          
          <div className="bg-white rounded-2xl p-8 shadow-md text-center">
            <h2 className="text-xl font-semibold text-ios-gray-900 mb-2">
              Ejercicio no encontrado
            </h2>
            <p className="text-ios-gray-600">
              No se pudo encontrar información sobre este ejercicio
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-ios-gray-50">
      {/* Header with Back Button */}
      <div className="bg-white shadow-ios mb-6">
        <div className="px-6 py-4">
          <button
            onClick={() => navigate(-1)}
            className="flex items-center text-ios-blue mb-4 active:scale-95 transition-all duration-200"
          >
            <svg
              className="w-5 h-5 mr-2"
              fill="currentColor"
              viewBox="0 0 20 20"
            >
              <path
                fillRule="evenodd"
                d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z"
                clipRule="evenodd"
              />
            </svg>
            Volver
          </button>

          <div className="flex items-start">
            <img
              src={getMuscleGroupIcon(exercise.muscleGroups[0])}
              alt={exercise.muscleGroups[0]}
              className="w-16 h-16 rounded-ios-lg mr-4 flex-shrink-0"
            />
            <div className="flex-1 min-w-0">
              <h1 className="text-2xl font-bold text-ios-gray-900 mb-2">
                {exercise.Ejercicio}
              </h1>
              <div className="flex flex-wrap gap-2">
                {exercise.muscleGroups.map((muscle, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-ios-blue bg-opacity-10 text-ios-blue text-sm rounded-full font-medium"
                  >
                    {muscle}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="px-6 space-y-4">
        {/* Basic Information */}
        <ExerciseInfoCard title="Información Básica">
          <div className="space-y-1">
            <InfoRow
              icon="nivel"
              label="Dificultad"
              value={exercise.Dificultad}
              valueColor={getDifficultyColor(exercise.Dificultad)}
            />
            <InfoRow
              icon="equipamiento"
              label="Equipamiento"
              value={exercise.Equipamiento}
            />
            <InfoRow
              icon="fuerza"
              label="Tipo de fuerza"
              value={exercise["Tipo de fuerza"]}
            />
            <InfoRow
              icon="musculos"
              label="Mecánica"
              value={exercise.Mecanica}
            />
          </div>
        </ExerciseInfoCard>

        {/* Muscle Groups */}
        <ExerciseInfoCard title="Grupos Musculares">
          <div className="space-y-3">
            <div>
              <h4 className="text-sm font-medium text-ios-gray-600 mb-2">
                Músculos Principales
              </h4>
              <div className="flex flex-wrap gap-2">
                {exercise.muscleGroups.map((muscle, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-ios-green bg-opacity-10 text-ios-green text-sm rounded-full font-medium"
                  >
                    {muscle}
                  </span>
                ))}
              </div>
            </div>

            {exercise.involvedMuscles.length > 0 && (
              <div>
                <h4 className="text-sm font-medium text-ios-gray-600 mb-2">
                  Músculos Involucrados
                </h4>
                <div className="flex flex-wrap gap-2">
                  {exercise.involvedMuscles.map((muscle, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-ios-orange bg-opacity-10 text-ios-orange text-sm rounded-full font-medium"
                    >
                      {muscle}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {exercise.specificParts.length > 0 && (
              <div>
                <h4 className="text-sm font-medium text-ios-gray-600 mb-2">
                  Partes Específicas
                </h4>
                <div className="flex flex-wrap gap-2">
                  {exercise.specificParts.map((part, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-ios-gray-100 text-ios-gray-700 text-sm rounded-full"
                    >
                      {part}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </ExerciseInfoCard>

        {/* Exercise Variations */}
        {exercise.variations.length > 1 && (
          <ExerciseInfoCard
            title={`Variaciones (${exercise.variations.length})`}
          >
            <div className="space-y-3">
              {exercise.variations.map((variation, index) => (
                <div key={index} className="p-3 bg-ios-gray-50 rounded-ios">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium text-ios-gray-900">
                      Variación {index + 1}
                    </span>
                    <div
                      className={`px-2 py-1 rounded-full text-xs font-medium ${getDifficultyBgColor(
                        variation.Dificultad
                      )} ${getDifficultyColor(variation.Dificultad)}`}
                    >
                      {variation.Dificultad}
                    </div>
                  </div>
                  <div className="text-sm text-ios-gray-600 space-y-1">
                    <div>Grupo muscular: {variation["Grupo muscular"]}</div>
                    <div>
                      Músculo involucrado: {variation["Músculo involucrado"]}
                    </div>
                    {variation["Parte específica"] && (
                      <div>
                        Parte específica: {variation["Parte específica"]}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </ExerciseInfoCard>
        )}

        {/* Related Exercises */}
        {relatedExercises.length > 0 && (
          <ExerciseInfoCard title="Ejercicios Relacionados">
            <div className="flex overflow-x-auto pb-2 -mx-1">
              {relatedExercises.map((relatedExercise, index) => (
                <div key={index} className="px-1 flex-shrink-0">
                  <RelatedExerciseCard exercise={relatedExercise} />
                </div>
              ))}
            </div>
          </ExerciseInfoCard>
        )}

        {/* Action Buttons */}
        <div className="grid grid-cols-2 gap-4 pb-6">
          <button
            onClick={() => navigate("/exercises")}
            className="flex items-center justify-center py-3 px-4 bg-ios-gray-100 text-ios-gray-700 rounded-ios font-medium transition-all duration-200 active:scale-95"
          >
            <img
              src={getAppIcon("ejercicios")}
              alt="Ejercicios"
              className="w-5 h-5 mr-2"
            />
            Ver más ejercicios
          </button>
          <button
            onClick={() => navigate("/muscle-groups")}
            className="flex items-center justify-center py-3 px-4 bg-ios-blue text-white rounded-ios font-medium transition-all duration-200 active:scale-95"
          >
            <img
              src={getAppIcon("musculos")}
              alt="Músculos"
              className="w-5 h-5 mr-2 filter brightness-0 invert"
            />
            Ver grupos musculares
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExerciseDetailScreen;
