import React, { useState, useEffect } from "react";

const ExerciseTimer = ({ series, descansoSegundos, onComplete }) => {
  const [currentSeries, setCurrentSeries] = useState(1);
  const [isResting, setIsResting] = useState(false);
  const [restTime, setRestTime] = useState(0);
  const [isCompleted, setIsCompleted] = useState(false);
  const [timerActive, setTimerActive] = useState(false);

  // Convertir tiempo de descanso de formato "60-90 segundos" a número
  const parseRestTime = (descansoStr) => {
    if (!descansoStr) return 60; // default
    const match = descansoStr.match(/(\d+)/);
    return match ? parseInt(match[1]) : 60;
  };

  const restSeconds = parseRestTime(descansoSegundos);

  useEffect(() => {
    let interval;
    if (isResting && restTime > 0) {
      interval = setInterval(() => {
        setRestTime((prev) => {
          if (prev <= 1) {
            setIsResting(false);
            setTimerActive(false);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isResting, restTime]);

  const startSeries = () => {
    setTimerActive(true);
    // Simular que la serie ha terminado inmediatamente y empieza el descanso
    if (currentSeries < series) {
      setIsResting(true);
      setRestTime(restSeconds);
    } else {
      // Es la última serie
      setIsCompleted(true);
      setTimerActive(false);
      if (onComplete) onComplete();
    }
  };

  const nextSeries = () => {
    if (currentSeries < series) {
      setCurrentSeries(prev => prev + 1);
      setTimerActive(false);
    }
  };

  const resetTimer = () => {
    setCurrentSeries(1);
    setIsResting(false);
    setRestTime(0);
    setIsCompleted(false);
    setTimerActive(false);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (isCompleted) {
    return (
      <div className="bg-white rounded-2xl p-6 shadow-md">
        <div className="text-center">
          <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">✅</span>
          </div>
          <h3 className="text-xl font-bold text-green-600 mb-2">
            ¡Ejercicio Completado!
          </h3>
          <p className="text-ios-gray-600 mb-4">
            Has completado todas las {series} series
          </p>
          <button
            onClick={resetTimer}
            className="bg-ios-blue text-white px-6 py-3 rounded-2xl font-medium transition-all duration-200 active:scale-95"
          >
            Repetir Ejercicio
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl p-6 shadow-md">
      <div className="text-center">
        {/* Contador de Series */}
        <div className="mb-6">
          <h3 className="text-2xl font-bold text-ios-gray-900 mb-2">
            Serie {currentSeries} de {series}
          </h3>
          <div className="flex justify-center space-x-2">
            {[...Array(series)].map((_, index) => (
              <div
                key={index}
                className={`w-3 h-3 rounded-full ${
                  index < currentSeries - 1
                    ? "bg-green-500"
                    : index === currentSeries - 1
                    ? "bg-ios-blue"
                    : "bg-ios-gray-200"
                }`}
              />
            ))}
          </div>
        </div>

        {/* Timer de Descanso */}
        {isResting && (
          <div className="mb-6">
            <div className="w-24 h-24 bg-ios-orange bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-3xl font-bold text-ios-orange">
                {restTime}
              </span>
            </div>
            <p className="text-lg font-semibold text-ios-orange mb-2">
              Descansando...
            </p>
            <p className="text-sm text-ios-gray-600">
              Tiempo de descanso: {formatTime(restTime)}
            </p>
          </div>
        )}

        {/* Botones de Control */}
        <div className="space-y-3">
          {!isResting && !timerActive && (
            <button
              onClick={startSeries}
              className="w-full bg-ios-blue text-white py-4 rounded-2xl font-semibold text-lg transition-all duration-200 active:scale-95"
            >
              {currentSeries === 1 ? "Iniciar Serie" : "Continuar Serie"}
            </button>
          )}

          {isResting && (
            <button
              disabled
              className="w-full bg-ios-gray-200 text-ios-gray-500 py-4 rounded-2xl font-semibold text-lg cursor-not-allowed"
            >
              Descansando... ({restTime}s)
            </button>
          )}

          {!isResting && restTime === 0 && timerActive && currentSeries < series && (
            <button
              onClick={nextSeries}
              className="w-full bg-green-500 text-white py-4 rounded-2xl font-semibold text-lg transition-all duration-200 active:scale-95"
            >
              Siguiente Serie
            </button>
          )}

          {currentSeries > 1 && (
            <button
              onClick={resetTimer}
              className="w-full bg-ios-gray-100 text-ios-gray-700 py-3 rounded-2xl font-medium transition-all duration-200 active:scale-95"
            >
              Reiniciar Ejercicio
            </button>
          )}
        </div>

        {/* Información adicional */}
        <div className="mt-6 pt-4 border-t border-ios-gray-100">
          <p className="text-sm text-ios-gray-600">
            Descanso entre series: {descansoSegundos}
          </p>
        </div>
      </div>
    </div>
  );
};

export default ExerciseTimer;
