};

// Mapeo específico para grupos musculares
const muscleGroupIconMapping = {
  Pecho: "pecho",
  Espalda: "espalda",
  Hombros: "hombros",
  Deltoides: "deltoides",
  Bíceps: "biceps",
  Tríceps: "tricep",
