import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getExercisesByDay } from '../utils/exerciseUtils';
import { iconMapping, muscleGroupIconMapping } from '../utils/iconMapping';

const Home = () => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [exercisesForDay, setExercisesForDay] = useState([]);

  // Días de la semana en español
  const daysOfWeek = ['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'];
  const monthNames = [
    'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
    'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
  ];

  // Mapeo de días en español para los datos
  const dayMapping = {
    'Lunes': 'Lun',
    'Martes': 'Mar', 
    'Miércoles': 'Mié',
    'Jueves': 'Jue',
    'Viernes': 'Vie',
    'Sábado': 'Sáb',
    'Domingo': 'Dom'
  };

  // Obtener el día de la semana en español
  const getDayInSpanish = (date) => {
    const dayIndex = date.getDay();
    const spanishDays = ['Domingo', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado'];
    return spanishDays[dayIndex];
  };

  // Cargar ejercicios cuando cambia la fecha seleccionada
  useEffect(() => {
    const dayName = getDayInSpanish(selectedDate);
    const dayData = getExercisesByDay(dayName);
    
    if (dayData && dayData.ejercicios) {
      // Aplanar todos los ejercicios del día
      const allExercisesForDay = [];
      dayData.ejercicios.forEach(grupo => {
        if (grupo.rutina) {
          grupo.rutina.forEach(ejercicio => {
            allExercisesForDay.push({
              ...ejercicio,
              grupoMuscular: grupo.nombre,
              diaCompleto: dayData.dia,
              musculosDelDia: dayData.musculos
            });
          });
        }
      });
      setExercisesForDay(allExercisesForDay);
    } else {
      setExercisesForDay([]);
    }
  }, [selectedDate]);

  // Generar días del calendario
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());
    
    const days = [];
    for (let i = 0; i < 42; i++) {
      const day = new Date(startDate);
      day.setDate(startDate.getDate() + i);
      days.push(day);
    }
    
    return days;
  };

  // Verificar si una fecha tiene ejercicios
  const hasExercises = (date) => {
    const dayName = getDayInSpanish(date);
    const dayData = getExercisesByDay(dayName);
    return dayData && dayData.ejercicios && dayData.ejercicios.length > 0;
  };

  // Obtener icono para un ejercicio
  const getExerciseIcon = (grupoMuscular) => {
    const iconKey = muscleGroupIconMapping[grupoMuscular] || 'musculos';
    return iconMapping[iconKey] || iconMapping.musculos;
  };

  // Manejar click en ejercicio
  const handleExerciseClick = (ejercicio) => {
    // Preparado para futuro: navegación al detalle del ejercicio
    navigate(`/exercise/${encodeURIComponent(ejercicio.nombre)}`);
  };

  const calendarDays = generateCalendarDays();

  return (
    <div className="min-h-screen bg-ios-gray-50">
      {/* Header */}
      <div className="bg-white sticky top-0 z-10 shadow-sm">
        <div className="px-4 py-3">
          <h1 className="text-2xl font-bold text-ios-gray-900 text-center">
            Rutina de Ejercicios
          </h1>
        </div>

        {/* Calendario */}
        <div className="px-4 pb-4">
          {/* Navegación del mes */}
          <div className="flex items-center justify-between mb-4">
            <button
              onClick={() => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1))}
              className="p-2 rounded-full hover:bg-ios-gray-100 transition-colors"
            >
              <svg className="w-5 h-5 text-ios-blue" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            
            <h2 className="text-lg font-semibold text-ios-gray-900">
              {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
            </h2>
            
            <button
              onClick={() => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1))}
              className="p-2 rounded-full hover:bg-ios-gray-100 transition-colors"
            >
              <svg className="w-5 h-5 text-ios-blue" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>

          {/* Días de la semana */}
          <div className="grid grid-cols-7 gap-1 mb-2">
            {daysOfWeek.map(day => (
              <div key={day} className="text-center py-2 text-sm font-medium text-ios-gray-500">
                {day}
              </div>
            ))}
          </div>

          {/* Días del calendario */}
          <div className="grid grid-cols-7 gap-1">
            {calendarDays.map((day, index) => {
              const isSelected = day.toDateString() === selectedDate.toDateString();
              const isToday = day.toDateString() === new Date().toDateString();
              const isCurrentMonth = day.getMonth() === currentMonth.getMonth();
              const hasWorkout = hasExercises(day);

              return (
                <button
                  key={index}
                  onClick={() => setSelectedDate(day)}
                  className={`
                    relative h-10 w-10 rounded-full text-sm font-medium transition-all duration-200
                    ${isSelected 
                      ? 'bg-ios-blue text-white shadow-lg' 
                      : isToday 
                        ? 'bg-ios-blue/10 text-ios-blue' 
                        : isCurrentMonth 
                          ? 'text-ios-gray-900 hover:bg-ios-gray-100' 
                          : 'text-ios-gray-400'
                    }
                  `}
                >
                  {day.getDate()}
                  {hasWorkout && (
                    <div className={`absolute -bottom-1 left-1/2 transform -translate-x-1/2 w-1 h-1 rounded-full ${
                      isSelected ? 'bg-white' : 'bg-ios-blue'
                    }`} />
                  )}
                </button>
              );
            })}
          </div>
        </div>
      </div>

      {/* Contenido Principal */}
      <div className="px-4 py-4">
        {/* Información del día seleccionado */}
        <div className="mb-4">
          <h3 className="text-lg font-semibold text-ios-gray-900 mb-1">
            {getDayInSpanish(selectedDate)}
          </h3>
          <p className="text-sm text-ios-gray-600">
            {selectedDate.toLocaleDateString('es-ES', { 
              weekday: 'long', 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric' 
            })}
          </p>
        </div>

        {/* Cards de ejercicios */}
        {exercisesForDay.length > 0 ? (
          <div className="space-y-4">
            <div className="bg-ios-blue/5 rounded-2xl p-4 mb-4">
              <h4 className="font-semibold text-ios-gray-900 mb-1">
                Músculos del día: {exercisesForDay[0]?.musculosDelDia}
              </h4>
              <p className="text-sm text-ios-gray-600">
                {exercisesForDay.length} ejercicio{exercisesForDay.length !== 1 ? 's' : ''} programado{exercisesForDay.length !== 1 ? 's' : ''}
              </p>
            </div>

            {exercisesForDay.map((ejercicio, index) => (
              <div
                key={index}
                onClick={() => handleExerciseClick(ejercicio)}
                className="bg-white rounded-2xl p-4 shadow-md hover:shadow-lg transition-shadow duration-200 cursor-pointer"
              >
                <div className="flex items-start space-x-4">
                  {/* Icono del grupo muscular */}
                  <div className="flex-shrink-0">
                    <img
                      src={getExerciseIcon(ejercicio.grupoMuscular)}
                      alt={ejercicio.grupoMuscular}
                      className="w-12 h-12 rounded-xl bg-ios-gray-50 p-2"
                    />
                  </div>

                  <div className="flex-1 min-w-0">
                    <h5 className="font-semibold text-ios-gray-900 mb-1 truncate">
                      {ejercicio.nombre}
                    </h5>
                    <p className="text-sm text-ios-gray-600 mb-2">
                      {ejercicio.grupoMuscular}
                    </p>

                    {/* Información del ejercicio */}
                    <div className="flex flex-wrap items-center gap-4 text-xs text-ios-gray-500">
                      <div className="flex items-center space-x-1">
                        <img src={iconMapping.series} alt="Series" className="w-4 h-4" />
                        <span>{ejercicio.series} series</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <img src={iconMapping.repeticiones} alt="Repeticiones" className="w-4 h-4" />
                        <span>{ejercicio.repeticiones} reps</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <img src={iconMapping.nivel} alt="Nivel" className="w-4 h-4" />
                        <span>{ejercicio.nivel}</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* GIF del ejercicio */}
                {ejercicio.gif && (
                  <div className="mt-4 rounded-xl overflow-hidden bg-ios-gray-50">
                    {ejercicio.gif.includes('.mp4') ? (
                      <video
                        src={`/gifs/${ejercicio.gif}`}
                        autoPlay
                        loop
                        muted
                        playsInline
                        className="w-full h-48 object-cover"
                        onError={(e) => {
                          e.target.style.display = 'none';
                        }}
                      />
                    ) : (
                      <img
                        src={`/gifs/${ejercicio.gif}`}
                        alt={ejercicio.nombre}
                        className="w-full h-48 object-cover"
                        onError={(e) => {
                          e.target.style.display = 'none';
                        }}
                      />
                    )}
                  </div>
                )}

                {/* Descanso */}
                <div className="mt-3 flex items-center justify-between">
                  <div className="flex items-center space-x-2 text-sm text-ios-gray-600">
                    <img src={iconMapping.descanso} alt="Descanso" className="w-4 h-4" />
                    <span>Descanso: {ejercicio.descanso}</span>
                  </div>
                  
                  <div className="text-ios-blue">
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="bg-white rounded-2xl p-8 shadow-md text-center">
            <img 
              src={iconMapping.descanso} 
              alt="Descanso" 
              className="w-16 h-16 mx-auto mb-4 opacity-50" 
            />
            <h4 className="font-semibold text-ios-gray-900 mb-2">
              Día de descanso
            </h4>
            <p className="text-sm text-ios-gray-600">
              No hay ejercicios programados para este día. ¡Disfruta tu descanso!
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
